import axios from "axios"
import {useState} from "react"
import {ToastContainer} from "react-toastify"
import {Toast} from "react-toastify"

import 'react-toastify/dist/ReactToastify.css'




const Form = () => {

	const [userData, setUserData] = useState({
		UserName : "",
		Email_Id : "",
		Phone_Number : "",
		Password : "",
		Confirm_Password : ""
	})
	const handleChange = (event) => {
		setUserData({...userData, [event.target.name]: event.target.value})

	}

	const handleSubmit = (event) => {
		event.preventDefault()
		if(Validation()){}
	}
	const Validation = () => {
		const {UserName, Email_ID, Phone_Number, Password, Confirm_Password} = userData
		if(UserName.length > 10) return false
		if(!Email_ID.includes("@gmail.com")) return false
	}

	return (
		<div>
			<h1> Register </h1>
			<form onSubmit={(e) => handleSubmit(e)}> 
				<input name="UserName" placeholder="UserName" required onChange={(e) => handleChange(e)} />
				<input name="Email_ID" placeholder="Email ID" required onChange={(e) => handleChange(e)} />
				<input name="Phone_Number" placeholder="Phone Number  (Optional)" onChange={(e) => handleChange(e)} />
				<input name="Password" placeholder="Password" required onChange={(e) => handleChange(e)} /> 
				<input name="Confirm_Password" placeholder="Confirm Password" required onChange={(e) => handleChange(e)} />
				<button> Submit </button>

			</form>
		</div>
	)
}

export default Form
